import React, {Component} from 'react';
import './Projects.css';
import $ from 'jquery';

class Projects extends Component{
    render() {
        return(
            <div  id="projects-section">
                <div className="container">
                    <div className="projects col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <h1 id="project-title">Projetos</h1>
                    </div> <br/>
                    <div align="center">
                        <button className="btn btn-default filter-button primary-button" id="web-button" data-filter="web">Web</button>
                        <button className="btn btn-default filter-button primary-button" id="mobile-button" data-filter="mobile">Mobile</button>
                        <button className="btn btn-default filter-button primary-button" id="desktop-button" data-filter="desktop">Desktop</button>
                    </div> <br/>

                    <div align="center">
                        <button className="btn btn-default filter-button secundary-button filter web react node php" data-filter="react">React</button>
                        <button className="btn btn-default filter-button secundary-button filter web react node php" data-filter="node">Node</button>
                        <button className="btn btn-default filter-button secundary-button filter web react node php" data-filter="php">PHP</button>
                        <button className="btn btn-default filter-button secundary-button filter mobile swift react-native flutter " data-filter="swift">Swift</button>
                        <button className="btn btn-default filter-button secundary-button filter mobile swift react-native flutter  " data-filter="react-native">React Native</button>
                        <button className="btn btn-default filter-button secundary-button filter mobile swift react-native flutter " data-filter="flutter">Flutter</button>
                        <button className="btn btn-default filter-button secundary-button filter desktop javascript-desktop " data-filter="javascript-desktop">Javascript</button>

                    </div> <br/>

                    <div className="row">

                        <div className="project col-lg-4 col-md-4 col-sm-4  col-xs-6 filter react">
                            <img  className="img-responsive" />
                        </div>

                        <div className="project col-lg-4 col-md-4 col-sm-4 port-image col-xs-6 filter web">
                            <img  className="img-responsive" />
                        </div>

                        <div className="project col-lg-4 col-md-4 col-sm-4 port-image col-xs-6 filter web">
                            <img   className="img-responsive" />
                        </div>

                        <div className="project col-lg-4 col-md-4 col-sm-4 port-image  col-xs-6 filter desktop">
                            <img   className="img-responsive" />
                        </div>

                        <div className="project col-lg-4 col-md-4 col-sm-4  col-xs-6 port-image filter desktop">
                            <img  className="img-responsiv" />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

$(document).ready(function () {

    $(".filter-button").click(function () {
        var value = $(this).attr('data-filter');

        switch (value) {
            case 'web':
                $('#web-button').css({'background-color': '#133B57', 'color': '#fff'});
                break
            case 'mobile':
                $('#mobile-button').css({'background-color': '#133B57', 'color': '#fff'});
            case 'desktop':
                $('#desktop-button').css({'background-color': '#133B57', 'color': '#fff'});
            default:


        }

        if (value == "hide") {
            //$('.filter').removeClass('hidden');
            $('.filter').hide('1000');

        }
        else {

//                $('.filter[filter-item="'+value+'"]').removeClass('hidden');
//                $(".filter").not('.filter[filter-item="'+value+'"]').addClass('hidden');
            $(".filter").not('.'+value).hide('3000');
            $(".filter").filter('.'+value).show('3000');
        }

    });

    if ($(".filter-button").removeClass("active")) {
        $(this).removeClass("active");
    }

});

export default Projects;
