import React, {Component} from 'react';
import './Projects.css';
import $ from 'jquery';

class Projects extends Component{
    render() {
        return(
            <div className="container">
                <div className="projects col-lg-2 col-md-12 col-sm-12 col-xs-12">
                    <h1 id="project-title">Projetos</h1>
                </div> <br/>
                <div align="center">
                    <button className="btn btn-default filter-button" data-filter="web">Web</button>
                    <button className="btn btn-default filter-button" data-filter="mobile">Mobile</button>
                    <button className="btn btn-default filter-button" data-filter="desktop">Desktop</button>
                </div> <br/>

                <div className="row">
                    <div className="project col-lg-4 col-md-4 col-sm-4  col-xs-6 filter web">
                        <img src="http://findmatthew.com/img/thumb-2.jpg" className="img-responsive" />
                    </div>

                    <div className="project col-lg-4 col-md-4 col-sm-4 port-image col-xs-6 filter web">
                        <img src="http://findmatthew.com/img/thumb-2.jpg" className="img-responsive" />
                    </div>

                    <div className="project col-lg-4 col-md-4 col-sm-4 port-image col-xs-6 filter web">
                        <img src="http://findmatthew.com/img/thumb-2.jpg"  className="img-responsive" />
                    </div>

                    <div className="project col-lg-4 col-md-4 col-sm-4 port-image  col-xs-6 filter desktop">
                        <img src="http://findmatthew.com/img/thumb-2.jpg"  className="img-responsive" />
                    </div>

                    <div className="project col-lg-4 col-md-4 col-sm-4  col-xs-6 port-image filter desktop">
                        <img src="http://findmatthew.com/img/thumb-2.jpg"  className="img-responsiv" />
                    </div>
                </div>
            </div>
        )
    }
    }

$(document).ready(function () {

    $(".filter-button").click(function () {
        var value = $(this).attr('data-filter');

        if (value == "all") {
          //$('.filter').removeClass('hidden');
            $('.filter').show('1000');

        }
        else {

//                $('.filter[filter-item="'+value+'"]').removeClass('hidden');
//                $(".filter").not('.filter[filter-item="'+value+'"]').addClass('hidden');
            $(".filter").not('.'+value).hide('3000');
            $(".filter").filter('.'+value).show('3000');
        }

    });

    if ($(".filter-button").removeClass("active")) {
        $(this).removeClass("active");
    }
    $(this).addClass("active");

});

export default Projects;
